  switch(action.type) {
    case 'ADD_TO_DO':
      let arr = [...state,action.todo]
      return arr;
      // don't mutate state here or the tests will fail
      return
    default:
      return state;
  }
};

const addToDo = (todo) => {
  return {
    type: 'ADD_TO_DO',
    todo
  }
}

const store = Redux.createStore(immutableReducer);

